import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import "./styles.css";
import axios from "axios";

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});


const User = () => {
  const [user, setUser] = useState({
    name: "",
    document: "",
    email: "",
    phone: "",
    birth_date: "",
    salary: "",
    created_at: "",
  });
  const { id } = useParams();
  useEffect(() => {
    loadUser();
  }, []);
  const loadUser = async () => {
    const res = await axios.get(
      `https://60decafeabbdd9001722d05c.mockapi.io/users/${id}`
    );
    setUser(res.data);
  };
  return (
    <div className="">
      <header className="">
      <Link to="/">
        back to Home
      </Link>
      <h1 className="display-4">Id: {id}</h1>
      <hr />

      

      {/* <ul className="list-group w-50">
        <li className="list-group-item">Nome: {user.name}</li>
        <li className="list-group-item">CPF: {user.document}</li>
        <li className="list-group-item">E-mail: {user.email}</li>
        <li className="list-group-item">Telefone: {user.phone}</li>
        <li className="list-group-item">Data de Nascimento: {user.birth_date}</li>
        <li className="list-group-item">Salário: {user.salary}</li>
        <li className="list-group-item">Data de Contratação: {user.birth_date}</li>   
      </ul> */}
      </header>
    </div>
  );
};

export default User;
